<?php

namespace AppBundle\Repository;

/**
 * AnswerRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class AnswerRepository extends \Doctrine\ORM\EntityRepository
{
    public function getAllChecked($answers)
    {
        return $this->createQueryBuilder('a')
            ->select('a')
            ->where('a.id IN (:ids)')
            ->setParameter('ids', $answers)
            ->distinct(true)
            ->getQuery()
            ->getResult();
    }

    public function getCorrectAnswers($qId)
    {
        return $this->createQueryBuilder('a')
            ->select('a.id')
            ->where('a.question = :qId')
            ->andWhere('a.correct = 1')
            ->setParameter('qId', $qId)
            ->getQuery()
            ->getResult();
    }

    public function removeAnswers($questionId)
    {
        $answers = $this->createQueryBuilder('a')
            ->select('a')
            ->where('a.question = :question')
            ->setParameter('question', $questionId)
            ->getQuery()
            ->getResult();

        foreach ($answers as $answer) {
            $this->_em->remove($answer);
        }
        $this->_em->flush();
    }
}
